package adventofcode.y2016.day2

object Main {

  val Input = Seq(
    "",
    "",
    "",
    "",
    ""
  )

  def part1: Int = {
    val moves = Input.map(Movement.parse)
    val codeButtons = moves.foldLeft(Seq.empty[Button]) {
      case (buttons, movements) =>
        val fromButton = buttons.lastOption.getOrElse(Button.Central)
        buttons :+ fromButton.move(movements)
    }
    codeButtons.foldLeft(0)(10 * _ + _.num)
  }

  def part2: String = {
    val moves = Input.map(Movement.parse)
    val codeButtons = moves.foldLeft(Seq.empty[AdvancedButton]) {
      case (buttons, movements) =>
        val fromButton = buttons.lastOption.getOrElse(AdvancedButton.Central)
        buttons :+ fromButton.move(movements)
    }
    codeButtons.map(_.toChar).mkString
  }

  def main(args: Array[String]): Unit = {
    println(s"The bathroom code is $part1")
    println(s"The bathroom code for the advanced keypad is $part2")
  }
}
